import React, { useState } from "react";

import {
  MenuItem,
  ProSidebar,
  SidebarContent,
  SidebarHeader,
  Menu,
} from "react-pro-sidebar";
import Icons from "../Components/Icons";
import dashboardIcon from "../Assests/dashboard.png";
import profileIcon from "../Assests/profile.png";
import chatIcon from "../Assests/chat.png";
import { FaHeart, FaAddressBook } from "react-icons/fa";
import { MdMenuOpen } from "react-icons/md";
import { FiMenu } from "react-icons/fi";
import sideBarBg from "../Assests/bg1.jpeg";

export default function Sidebar({ image, collapsed, handleToggleSidebar }) {
  const [menu, setMenu] = useState(true);

  const menuHandler = () => {
    setMenu((prev) => !prev);
  };
  return (
    <ProSidebar image={sideBarBg} collapsed={menu}>
      <SidebarHeader
        style={{
          textTransform: "uppercase",
          fontWeight: "bold",
          fontSize: 24,
          letterSpacing: "1px",
          overflow: "hidden",
          textOverflow: "ellipsis",
          whiteSpace: "nowrap",
        }}
      >
        <Menu>
          {menu ? (
            <MenuItem onClick={menuHandler} icon={<FiMenu />}></MenuItem>
          ) : (
            <MenuItem onClick={menuHandler} icon={<MdMenuOpen />}></MenuItem>
          )}
        </Menu>
      </SidebarHeader>
      <SidebarContent>
        <Menu iconShape="circle">
          <MenuItem
            suffix={<p>New</p>}
            icon={<Icons img={dashboardIcon} alt="Dashboard" />}
          >
            Dashboard
          </MenuItem>
          <MenuItem icon={<Icons img={profileIcon} alt="Profile" />}>
            Profile
          </MenuItem>

          <MenuItem icon={<Icons img={chatIcon} alt="Chat" />}>
            Message
          </MenuItem>

          <MenuItem icon={<FaHeart />}>Favourite</MenuItem>
        </Menu>
      </SidebarContent>
    </ProSidebar>
  );
}
