import React, { useState } from "react";

import {
  MenuItem,
  ProSidebar,
  SidebarContent,
  SidebarHeader,
  Menu,
} from "react-pro-sidebar";
import Icons from "../Components/Icons";
import dashboardIcon from "../Assests/dashboard.png";
import profileIcon from "../Assests/profile.png";
import chatIcon from "../Assests/chat.png";
import { FaHeart, FaAddressBook } from "react-icons/fa";
import { MdMenuOpen } from "react-icons/md";
import { FiMenu } from "react-icons/fi";
import sideBarBg from "../Assests/bg1.jpeg";
import { Route, Routes, Link } from "react-router-dom";
import { Dashboard } from "./Screens/Dashboard";
import Profile from "./Screens/Profile";

export default function Sidebar({
  image,
  collapsed,
  handleToggleSidebar,
  dashBoardHandler,
  profileHandler,
}) {
  return (
    <ProSidebar image={sideBarBg} collapsed={collapsed}>
      <SidebarHeader
        style={{
          textTransform: "uppercase",
          fontWeight: "bold",
          fontSize: 24,
          letterSpacing: "1px",
          overflow: "hidden",
          textOverflow: "ellipsis",
          whiteSpace: "nowrap",
        }}
      >
        <Menu>
          {collapsed ? (
            <MenuItem
              onClick={handleToggleSidebar}
              icon={<FiMenu />}
            ></MenuItem>
          ) : (
            <MenuItem
              onClick={handleToggleSidebar}
              icon={<MdMenuOpen />}
            ></MenuItem>
          )}
        </Menu>
      </SidebarHeader>
      <SidebarContent>
        <Menu iconShape="circle">
          <MenuItem
            onClick={() => dashBoardHandler}
            suffix={<p>New</p>}
            icon={<Icons img={dashboardIcon} alt="Dashboard" />}
          >
            Dashboard
          </MenuItem>
          <MenuItem
            onClick={() => profileHandler}
            icon={<Icons img={profileIcon} alt="Profile" />}
          >
            Profile
          </MenuItem>

          <MenuItem icon={<Icons img={chatIcon} alt="Chat" />}>
            Message
          </MenuItem>

          <MenuItem icon={<FaHeart />}>Favourite</MenuItem>
        </Menu>
      </SidebarContent>
      <Routes>
        <Route path="/" element={<Dashboard />} />
        <Route path="/profile" element={<Profile />} />
      </Routes>
    </ProSidebar>
  );
}
